import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { setupAuth, isAuthenticated } from "./supabaseAuth";
import { supabase } from "./db"; // Add this import
import { MessagingWebSocket } from "./websocket";
import { insertProfileSchema, insertRatingSchema, insertSwipeSchema, insertChallengeResponseSchema, insertMatchConversationSchema, insertMessageSchema } from "@shared/schema";
import { upload, processProfileImage, verifyProfileImage, deleteProfileImage } from "./upload";
import { submitForVerification, getVerificationStatus, getVerificationGuidelines, uploadToSupabaseStorage } from "./imageVerification";
import { seedAchievements, checkUserAchievements } from "./achievementService";
import { z } from "zod";
import Stripe from "stripe";
import express from "express";
import path from "path";

import { supabaseAdmin } from "./db"; 


if (!process.env.STRIPE_SECRET_KEY) {
  throw new Error('Missing required Stripe secret: STRIPE_SECRET_KEY');
}
const stripe = new Stripe(process.env.STRIPE_SECRET_KEY, {
  apiVersion: "2024-09-30.acacia",
});

export async function registerRoutes(app: Express): Promise<Server> {
  // Static file serving for uploads
  app.use('/uploads', express.static(path.join(process.cwd(), 'uploads')));

  // Initialize achievements
  await seedAchievements();

  // Auth middleware
  await setupAuth(app);

  // Auth routes
  // Update your /api/auth/user route to handle both cases
 app.get('/api/auth/user', isAuthenticated, async (req: any, res) => {
  try {
    const userId = req.user.claims.sub;

    console.log('Fetching user data for:', userId);

    // Try to get user from database
    let user = await storage.getUser(userId);

    // If user doesn't exist in database, create them
    if (!user) {
      console.log('User not found in database, creating new user');

      // Create user in database using data from JWT token
      user = await storage.upsertUser({
        id: userId,
        email: req.user.claims.email || '',
        firstName: req.user.claims.first_name || '',
        lastName: req.user.claims.last_name || '',
        profileImageUrl: req.user.claims.profile_image_url || null,
      });

      console.log('Created new user:', user);
    }

    // Get user profile (might not exist for new users)
    const profile = await storage.getProfile(userId);

    console.log('User profile:', profile ? 'found' : 'not found');

    // Check for new achievements (only if user exists)
    try {
      await checkUserAchievements(userId);
    } catch (achievementError) {
      console.warn('Achievement check failed:', achievementError);
      // Don't fail the whole request if achievements fail
    }

    const responseData = {
      ...user,
      profile,
    };

    console.log('Returning user data:', {
      hasUser: !!user,
      hasProfile: !!profile,
      userId: user?.id,
      userEmail: user?.email
    });

    res.json(responseData);
  } catch (error) {
    console.error("Error fetching user:", error);
    res.status(500).json({ message: "Failed to fetch user" });
  }
});

  // Profile routes
  app.get('/api/profiles/me', isAuthenticated, async (req: any, res) => {
    try {

      const userId = req.user.claims.sub;
      const profile = await storage.getProfile(userId);
      console.log("User Profile:", profile);
      if (!profile) {
        return res.status(404).json({ message: "Profile not found" });
      }
      res.json(profile);
    } catch (error) {
      console.error("Error fetching profile:", error);
      res.status(500).json({ message: "Failed to fetch profile" });
    }
  });

  app.post('/api/profiles', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const profileData = insertProfileSchema.parse({
        ...req.body,
        userId,
      });

      const existingProfile = await storage.getProfile(userId);
      if (existingProfile) {
        const updatedProfile = await storage.updateProfile(userId, profileData);

        // Check for new achievements after profile update
        await checkUserAchievements(userId);

        res.json(updatedProfile);
      } else {
        const newProfile = await storage.createProfile(profileData);

        // Check for new achievements after profile update
        await checkUserAchievements(userId);

        res.json(newProfile);
      }
    } catch (error) {
      console.error("Error creating/updating profile:", error);
      res.status(400).json({ message: "Failed to create/update profile" });
    }
  });

  app.get('/api/profiles/me', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const profile = await storage.getProfile(userId);

      if (!profile) {
        // Create a default profile if none exists
        const defaultProfile = {
          userId,
          age: 25,
          gender: 'male',
          occupation: '',
          bio: '',
          location: '',
        };
        const newProfile = await storage.createProfile(defaultProfile);
        return res.json(newProfile);
      }

      res.json(profile);
    } catch (error) {
      console.error("Error fetching my profile:", error);
      res.status(500).json({ message: "Failed to fetch profile" });
    }
  });

  app.get('/api/profiles/:userId', isAuthenticated, async (req, res) => {
    try {
      const { userId } = req.params;
      const profile = await storage.getProfileWithDetails(userId);

      if (!profile) {
        return res.status(404).json({ message: "Profile not found" });
      }

      res.json(profile);
    } catch (error) {
      console.error("Error fetching profile:", error);
      res.status(500).json({ message: "Failed to fetch profile" });
    }
  });

  // Discovery routes
  app.get('/api/discover', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const limit = parseInt(req.query.limit as string) || 10;

      const profiles = await storage.getDiscoverableProfiles(userId, limit);
      res.json(profiles);
    } catch (error) {
      console.error("Error fetching discoverable profiles:", error);
      res.status(500).json({ message: "Failed to fetch profiles" });
    }
  });

  // Rating routes
  app.post('/api/ratings', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const userProfile = await storage.getProfile(userId);

      if (!userProfile || userProfile.gender !== 'female') {
        return res.status(403).json({ message: "Only women can rate profiles" });
      }

      const ratingData = insertRatingSchema.parse({
        ...req.body,
        raterId: userId,
      });

      const rating = await storage.createRating(ratingData);
      res.json(rating);
    } catch (error) {
      console.error("Error creating rating:", error);
      res.status(400).json({ message: "Failed to create rating" });
    }
  });

  app.get('/api/ratings/:userId', isAuthenticated, async (req, res) => {
    try {
      const { userId } = req.params;
      const ratings = await storage.getRatingsForUser(userId);
      res.json(ratings);
    } catch (error) {
      console.error("Error fetching ratings:", error);
      res.status(500).json({ message: "Failed to fetch ratings" });
    }
  });

  app.get('/api/my-ratings', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const ratings = await storage.getUserRatings(userId);
      res.json(ratings);
    } catch (error) {
      console.error("Error fetching user ratings:", error);
      res.status(500).json({ message: "Failed to fetch user ratings" });
    }
  });

  // Swipe routes
  app.post('/api/swipes', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const swipeData = insertSwipeSchema.parse({
        ...req.body,
        swiperId: userId,
      });

      // Check if already swiped
      const hasSwipped = await storage.hasUserSwiped(userId, swipeData.swipedUserId);
      if (hasSwipped) {
        return res.status(400).json({ message: "Already swiped on this user" });
      }

      const swipe = await storage.createSwipe(swipeData);

      // Check for mutual likes to create match
      if (swipeData.liked) {
        const reciprocalSwipes = await storage.getSwipesBetweenUsers(swipeData.swipedUserId, userId);
        const mutualLike = reciprocalSwipes.find(s => s.liked);

        if (mutualLike) {
          await storage.createMatch(userId, swipeData.swipedUserId);
          res.json({ ...swipe, matched: true });
        } else {
          res.json({ ...swipe, matched: false });
        }
      } else {
        res.json({ ...swipe, matched: false });
      }
    } catch (error) {
      console.error("Error creating swipe:", error);
      res.status(400).json({ message: "Failed to create swipe" });
    }
  });

  // Match routes
  app.get('/api/matches', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const matches = await storage.getUserMatches(userId);
      res.json(matches);
    } catch (error) {
      console.error("Error fetching matches:", error);
      res.status(500).json({ message: "Failed to fetch matches" });
    }
  });

  // Photo upload routes
  app.post('/api/upload-photo', isAuthenticated, upload.single('photo'), async (req: any, res) => {
  try {
    const userId = req.user.claims.sub;

    if (!req.file) {
      return res.status(400).json({ message: "Aucun fichier t√©l√©charg√©" });
    }

    // Check if user can upload (not currently pending verification)
    const verificationStatus = await getVerificationStatus(userId);
    if (!verificationStatus.canUpload) {
      return res.status(400).json({ 
        message: "Vous avez d√©j√† une photo en cours de v√©rification" 
      });
    }

    // Verify image quality and content
    const verification = await verifyProfileImage(req.file.buffer);
    if (!verification.isValid) {
      return res.status(400).json({ 
        message: "Photo non valide",
        issues: verification.issues 
      });
    }

    // Upload to Supabase Storage instead of local processing
    const { imageUrl, thumbnailUrl, imagePath } = await uploadToSupabaseStorage(req.file, userId);

    // Submit for verification with Supabase URLs
    await submitForVerification(userId, imageUrl, imagePath);

    res.json({
      message: "Photo t√©l√©charg√©e avec succ√®s",
      imageUrl: imageUrl,
      thumbnailUrl: thumbnailUrl,
      verification,
    });
  } catch (error: any) {
    console.error("Error uploading photo:", error);
    res.status(500).json({ message: error.message || "Erreur lors du t√©l√©chargement" });
  }
});

  app.delete('/api/profile-photo', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);

      if (user?.profileImagePath) {
        // Delete from Supabase Storage
        try {
          const { error: deleteError } = await supabaseAdmin.storage
            .from('profile-images')
            .remove([user.profileImagePath]);

          if (deleteError) {
            console.warn('Failed to delete from Supabase storage:', deleteError);
          } else {
            console.log('Successfully deleted from Supabase storage:', user.profileImagePath);
          }
        } catch (storageError) {
          console.warn('Storage deletion error:', storageError);
        }
      }

      // Update user profile
      await storage.updateUser(userId, {
        profileImageUrl: null,
        profileImagePath: null
      });

      // Also update profile if it exists
      const profile = await storage.getProfile(userId);
      if (profile) {
        await storage.updateProfile(userId, {
          profileImageUrl: null,
          profileImageThumbnailUrl: null,
          isProfileImageVerified: false,
          profileImageVerificationStatus: "no_profile", // Reset to no_profile
        });
      }

      res.json({ message: "Photo supprim√©e avec succ√®s" });
    } catch (error) {
      console.error("Error deleting photo:", error);
      res.status(500).json({ message: "Erreur lors de la suppression" });
    }
  });

  app.get('/api/verification-status', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const status = await getVerificationStatus(userId);
      res.json(status);
    } catch (error) {
      console.error("Error fetching verification status:", error);
      res.status(500).json({ message: "Failed to fetch verification status" });
    }
  });

  app.get('/api/verification-guidelines', (req, res) => {
    res.json({
      guidelines: getVerificationGuidelines(),
    });
  });

  app.delete('/api/profile-photo', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const profile = await storage.getProfile(userId);

      if (!profile?.profileImageUrl) {
        return res.status(404).json({ message: "Aucune photo √† supprimer" });
      }

      // Delete image files
      const filename = profile.profileImageUrl.split('/').pop();
      if (filename) {
        await deleteProfileImage(filename);
      }

      // Update profile
      await storage.updateProfile(userId, {
        profileImageUrl: null,
        profileImageThumbnailUrl: null,
        isProfileImageVerified: false,
        profileImageVerificationStatus: "pending",
      });

      res.json({ message: "Photo supprim√©e avec succ√®s" });
    } catch (error) {
      console.error("Error deleting photo:", error);
      res.status(500).json({ message: "Erreur lors de la suppression" });
    }
  });

  // Stripe payment routes
  app.post("/api/create-payment-intent", isAuthenticated, async (req: any, res) => {
    try {
      const { amount } = req.body;
      const paymentIntent = await stripe.paymentIntents.create({
        amount: Math.round(amount * 100), // Convert to cents
        currency: "eur",
        automatic_payment_methods: {
          enabled: true,
        },
      });
      res.json({ clientSecret: paymentIntent.client_secret });
    } catch (error: any) {
      console.error("Error creating payment intent:", error);
      res.status(500).json({ message: "Error creating payment intent: " + error.message });
    }
  });

  // Stripe Checkout Session route
  app.post('/api/subscribe', isAuthenticated, async (req: any, res) => {
    try {
      console.log("Creating checkout session for user:", req.user);
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);

      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      if (user.stripeSubscriptionId) {
        const subscription = await stripe.subscriptions.retrieve(user.stripeSubscriptionId);

        if (subscription.status === 'active') {
          return res.json({
            message: "Already subscribed",
            url: `${process.env.REPLIT_DEV_DOMAIN ? `https://${process.env.REPLIT_DEV_DOMAIN}` : 'https://trustmarch.com'}/premium`
          });
        }
      }

      if (!user.email) {
        return res.status(400).json({ message: 'No user email on file' });
      }

      let customerId = user.stripeCustomerId;

      if (!customerId) {
        const customer = await stripe.customers.create({
          email: user.email,
          name: `${user.firstName} ${user.lastName}`,
        });
        customerId = customer.id;
        await storage.updateUserStripeInfo(userId, customerId, null);
      }

      const baseUrl = 'https://trustmarch.com';

      const session = await stripe.checkout.sessions.create({
        mode: 'subscription',
        customer: customerId,
        payment_method_types: ['card'],
        line_items: [{
          price_data: {
            currency: 'eur',
            product_data: {
              name: 'TrustMatch Premium',
              description: 'Abonnement mensuel Premium pour TrustMatch',
            },
            unit_amount: 999, // 9.99 EUR
            recurring: {
              interval: 'month',
            },
          },
          quantity: 1,
        }],
        success_url: `${baseUrl}/premium-success?session_id={CHECKOUT_SESSION_ID}&payment_success=true`,
        cancel_url: `${baseUrl}/premium`,
        metadata: {
          userId: userId,
        },
      });

      res.json({ url: session.url });
    } catch (error: any) {
      console.error("Error creating checkout session:", error);
      res.status(500).json({ message: "Error creating checkout session: " + error.message });
    }
  });

  // Activate premium after successful payment
  app.post('/api/activate-premium', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const { sessionId } = req.body;

      if (sessionId) {
        // Verify session with Stripe
        const session = await stripe.checkout.sessions.retrieve(sessionId);

        if (session.payment_status === 'paid' && session.mode === 'subscription') {
          // Update user to premium
          await storage.updateUser(userId, { 
            isPremium: true, 
            subscriptionStatus: 'active',
            stripeCustomerId: session.customer as string,
            stripeSubscriptionId: session.subscription as string
          });

          console.log(`User ${userId} activated premium via session ${sessionId}`);
          res.json({ success: true, message: "Premium activated successfully" });
        } else {
          res.status(400).json({ message: "Payment not completed" });
        }
      } else {
        res.status(400).json({ message: "Session ID required" });
      }
    } catch (error: any) {
      console.error("Error activating premium:", error);
      res.status(500).json({ message: "Error activating premium" });
    }
  });

  // Check subscription status
  app.get('/api/subscription-status', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);

      if (!user || !user.stripeSubscriptionId) {
        return res.json({ status: 'free', subscribed: false });
      }

      const subscription = await stripe.subscriptions.retrieve(user.stripeSubscriptionId);

      res.json({
        status: subscription.status,
        subscribed: subscription.status === 'active',
        currentPeriodEnd: subscription.current_period_end,
      });
    } catch (error: any) {
      console.error("Error checking subscription status:", error);
      res.status(500).json({ message: "Error checking subscription status" });
    }
  });

  // Mini-challenge routes
  app.get('/api/mini-challenges', isAuthenticated, async (req, res) => {
    try {
      const { category } = req.query;
      const challenges = await storage.getMiniChallenges(category as string);
      res.json(challenges);
    } catch (error) {
      console.error("Error fetching mini-challenges:", error);
      res.status(500).json({ message: "Failed to fetch mini-challenges" });
    }
  });

  app.get('/api/random-challenge', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const userResponses = await storage.getUserChallengeResponses(userId);
      const excludeAnswered = userResponses.map(r => r.challengeId.toString());

      const challenge = await storage.getRandomChallenge(excludeAnswered);
      if (!challenge) {
        return res.status(404).json({ message: "Aucun d√©fi disponible" });
      }

      res.json(challenge);
    } catch (error) {
      console.error("Error fetching random challenge:", error);
      res.status(500).json({ message: "Failed to fetch random challenge" });
    }
  });

  app.post('/api/challenge-responses', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const responseData = insertChallengeResponseSchema.parse({
        ...req.body,
        userId,
      });

      const response = await storage.createChallengeResponse(responseData);
      res.json(response);
    } catch (error) {
      console.error("Error creating challenge response:", error);
      res.status(400).json({ message: "Failed to create challenge response" });
    }
  });

  app.get('/api/my-challenge-responses', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const responses = await storage.getUserChallengeResponses(userId);
      res.json(responses);
    } catch (error) {
      console.error("Error fetching user challenge responses:", error);
      res.status(500).json({ message: "Failed to fetch user challenge responses" });
    }
  });

  app.post('/api/match-conversations', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const conversationData = insertMatchConversationSchema.parse({
        ...req.body,
        initiatorId: userId,
      });

      const conversation = await storage.createMatchConversation(conversationData);
      res.json(conversation);
    } catch (error) {
      console.error("Error creating match conversation:", error);
      res.status(400).json({ message: "Failed to create match conversation" });
    }
  });

  app.get('/api/match-conversations/:matchId', isAuthenticated, async (req, res) => {
    try {
      const { matchId } = req.params;
      const conversations = await storage.getMatchConversations(matchId);
      res.json(conversations);
    } catch (error) {
      console.error("Error fetching match conversations:", error);
      res.status(500).json({ message: "Failed to fetch match conversations" });
    }
  });

  // Achievement routes
  app.get('/api/achievements', isAuthenticated, async (req, res) => {
    try {
      const achievements = await storage.getAchievements();
      res.json(achievements);
    } catch (error) {
      console.error("Error fetching achievements:", error);
      res.status(500).json({ message: "Failed to fetch achievements" });
    }
  });

  app.get('/api/my-achievements', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const userAchievements = await storage.getUserAchievements(userId);
      res.json(userAchievements);
    } catch (error) {
      console.error("Error fetching user achievements:", error);
      res.status(500).json({ message: "Failed to fetch user achievements" });
    }
  });

  app.get('/api/profile-completion', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const completion = await storage.getProfileCompletionScore(userId);
      res.json(completion);
    } catch (error) {
      console.error("Error fetching profile completion:", error);
      res.status(500).json({ message: "Failed to fetch profile completion" });
    }
  });

  app.post('/api/check-achievements', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      await storage.checkAndUpdateAchievements(userId);
      res.json({ message: "Achievements checked successfully" });
    } catch (error) {
      console.error("Error checking achievements:", error);
      res.status(500).json({ message: "Failed to check achievements" });
    }
  });

  // Messaging API endpoints
  app.get('/api/messages/:matchId', isAuthenticated, async (req: any, res) => {
    try {
      const { matchId } = req.params;
      const { limit = 50 } = req.query;
      const messages = await storage.getMessages(matchId, parseInt(limit));
      res.json(messages);
    } catch (error) {
      console.error("Error fetching messages:", error);
      res.status(500).json({ message: "Failed to fetch messages" });
    }
  });

  app.post('/api/messages', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const messageData = insertMessageSchema.parse({
        ...req.body,
        senderId: userId,
      });

      const message = await storage.sendMessage(messageData);
      res.json(message);
    } catch (error) {
      console.error("Error sending message:", error);
      res.status(400).json({ message: "Failed to send message" });
    }
  });

  app.put('/api/messages/:messageId/read', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const { messageId } = req.params;
      await storage.markMessageAsRead(parseInt(messageId), userId);
      res.json({ success: true });
    } catch (error) {
      console.error("Error marking message as read:", error);
      res.status(500).json({ message: "Failed to mark message as read" });
    }
  });

  app.get('/api/unread-count', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const count = await storage.getUnreadMessageCount(userId);
      res.json({ count });
    } catch (error) {
      console.error("Error fetching unread count:", error);
      res.status(500).json({ message: "Failed to fetch unread count" });
    }
  });

  app.delete('/api/messages/:messageId', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const { messageId } = req.params;
      await storage.deleteMessage(parseInt(messageId), userId);
      res.json({ success: true });
    } catch (error) {
      console.error("Error deleting message:", error);
      res.status(500).json({ message: "Failed to delete message" });
    }
  });

  // Message reactions
  app.post('/api/messages/:messageId/reactions', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const { messageId } = req.params;
      const { reactionType } = req.body;

      const reaction = await storage.addMessageReaction({
        messageId: parseInt(messageId),
        userId,
        reactionType,
      });
      res.json(reaction);
    } catch (error) {
      console.error("Error adding reaction:", error);
      res.status(400).json({ message: "Failed to add reaction" });
    }
  });

  app.delete('/api/messages/:messageId/reactions', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const { messageId } = req.params;
      await storage.removeMessageReaction(parseInt(messageId), userId);
      res.json({ success: true });
    } catch (error) {
      console.error("Error removing reaction:", error);
      res.status(500).json({ message: "Failed to remove reaction" });
    }
  });

  // Subscription status route
  app.get("/api/subscription-status", isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);

      const subscribed = user?.subscriptionStatus === 'active';

      res.json({
        subscribed,
        status: user?.subscriptionStatus || 'free',
        stripeCustomerId: user?.stripeCustomerId || null,
        stripeSubscriptionId: user?.stripeSubscriptionId || null,
      });
    } catch (error) {
      console.error("Error getting subscription status:", error);
      res.status(500).json({ message: "Failed to get subscription status" });
    }
  });

  // Stripe payment routes
  app.post("/api/create-payment-intent", isAuthenticated, async (req: any, res) => {
    try {
      const { amount } = req.body;
      const stripe = require('stripe')(process.env.STRIPE_SECRET_KEY);

      const paymentIntent = await stripe.paymentIntents.create({
        amount: Math.round(amount * 100), // Convert to cents
        currency: "eur",
        automatic_payment_methods: {
          enabled: true,
        },
      });

      res.json({ clientSecret: paymentIntent.client_secret });
    } catch (error: any) {
      console.error("Error creating payment intent:", error);
      res.status(500).json({ message: "Error creating payment intent: " + error.message });
    }
  });



  // Stripe webhook handler
  app.post('/api/stripe-webhook', async (req, res) => {
    console.log("Webhook received:", req.body);
    console.log("Headers:", req.headers);
    const sig = req.headers['stripe-signature'];

    let event;
    try {
      // If webhook secret is available, verify signature
      if (process.env.STRIPE_WEBHOOK_SECRET) {
        event = stripe.webhooks.constructEvent(req.body, sig, process.env.STRIPE_WEBHOOK_SECRET);
      } else {
        // For development/testing, use the raw event body
        console.log("No webhook secret configured, using raw event body");
        event = req.body;
      }
    } catch (err) {
      console.log('Webhook signature verification failed.', err.message);
      return res.status(400).send('Webhook signature verification failed');
    }

    // Handle the event
    switch (event.type) {
      case 'checkout.session.completed':
        const session = event.data.object;
        const userId = session.metadata?.userId;

        if (userId && session.mode === 'subscription') {
          console.log(`Updating user ${userId} to premium status from checkout session`);
          // Update user premium status
          await storage.updateUser(userId, { isPremium: true, subscriptionStatus: 'active' });

          // Store subscription ID if available
          if (session.subscription) {
            await storage.updateUserStripeInfo(userId, session.customer, session.subscription);
          }
          console.log(`User ${userId} successfully updated to premium`);
        }
        break;

      case 'invoice.payment_succeeded':
        const paymentIntent = event.data.object;
        const subscriptionId = paymentIntent.subscription;

        if (subscriptionId) {
          // Find user with this subscription ID and update status
          const subscription = await stripe.subscriptions.retrieve(subscriptionId);
          const customerId = subscription.customer;

          // Find user by stripe customer ID
          const user = await storage.getUserByStripeCustomerId(customerId);
          if (user) {
            await storage.updateUser(user.id, { subscriptionStatus: 'active', isPremium: true });
          }
        }
        break;

      case 'customer.subscription.deleted':
        const deletedSubscription = event.data.object;
        const deletedCustomerId = deletedSubscription.customer;

        const canceledUser = await storage.getUserByStripeCustomerId(deletedCustomerId);
        if (canceledUser) {
          await storage.updateUser(canceledUser.id, { subscriptionStatus: 'canceled', isPremium: false });
        }
        break;

      default:
        console.log(`Unhandled event type ${event.type}`);
    }

    res.json({ received: true });
  });

  // Auth routes for Supabase
  app.post('/api/auth/signup', async (req, res) => {
    try {
      const { email, password, firstName, lastName } = req.body;

      const { data, error } = await supabase.auth.signUp({
        email,
        password,
        options: {
          data: {
            first_name: firstName,
            last_name: lastName,
          }
        }
      });

      if (error) {
        return res.status(400).json({ message: error.message });
      }

      res.json({ user: data.user, message: "Check your email for verification link" });
    } catch (error) {
      console.error("Signup error:", error);
      res.status(500).json({ message: "Signup failed" });
    }
  });

  app.post('/api/auth/signin', async (req, res) => {
    try {
      const { email, password } = req.body;

      const { data, error } = await supabase.auth.signInWithPassword({
        email,
        password,
      });

      if (error) {
        return res.status(400).json({ message: error.message });
      }

      res.json({ 
        user: data.user, 
        session: data.session,
        access_token: data.session?.access_token 
      });
    } catch (error) {
      console.error("Signin error:", error);
      res.status(500).json({ message: "Signin failed" });
    }
  });

  app.post('/api/auth/signout', async (req, res) => {
    try {
      const { error } = await supabase.auth.signOut();

      if (error) {
        return res.status(400).json({ message: error.message });
      }

      res.json({ message: "Signed out successfully" });
    } catch (error) {
      console.error("Signout error:", error);
      res.status(500).json({ message: "Signout failed" });
    }
  });


  app.post('/api/auth/signup', async (req, res) => {
    try {
      const { email, password, firstName, lastName } = req.body;

      const { data, error } = await supabaseAdmin.auth.admin.createUser({
        email,
        password,
        user_metadata: {
          first_name: firstName,
          last_name: lastName,
        },
        email_confirm: true, // Auto-confirm for admin creation
      });

      if (error) {
        return res.status(400).json({ message: error.message });
      }

      res.json({ user: data.user, message: "Account created successfully" });
    } catch (error) {
      console.error("Signup error:", error);
      res.status(500).json({ message: "Signup failed" });
    }
  });

  app.post('/api/auth/signin', async (req, res) => {
    try {
      const { email, password } = req.body;

      const { data, error } = await supabaseAdmin.auth.signInWithPassword({
        email,
        password,
      });

      if (error) {
        return res.status(400).json({ message: error.message });
      }

      res.json({ 
        user: data.user, 
        session: data.session,
        access_token: data.session?.access_token 
      });
    } catch (error) {
      console.error("Signin error:", error);
      res.status(500).json({ message: "Signin failed" });
    }
  });

  app.post('/api/auth/signout', async (req, res) => {
    try {
      const { error } = await supabaseAdmin.auth.signOut();

      if (error) {
        return res.status(400).json({ message: error.message });
      }

      res.json({ message: "Signed out successfully" });
    } catch (error) {
      console.error("Signout error:", error);
      res.status(500).json({ message: "Signout failed" });
    }
  });

  app.post('/api/auth/refresh', async (req, res) => {
    try {
      const { refresh_token } = req.body;

      const { data, error } = await supabaseAdmin.auth.refreshSession({
        refresh_token
      });

      if (error) {
        return res.status(400).json({ message: error.message });
      }

      res.json({ session: data.session });
    } catch (error) {
      console.error("Refresh error:", error);
      res.status(500).json({ message: "Token refresh failed" });
    }
  });

  app.post('/api/auth/refresh', async (req, res) => {
    try {
      const { refresh_token } = req.body;

      const { data, error } = await supabase.auth.refreshSession({
        refresh_token
      });

      if (error) {
        return res.status(400).json({ message: error.message });
      }

      res.json({ session: data.session });
    } catch (error) {
      console.error("Refresh error:", error);
      res.status(500).json({ message: "Token refresh failed" });
    }
  });

  // Note: Catch-all route removed - Vite handles this in development

  const httpServer = createServer(app);

  // Initialize WebSocket server
  const messagingWS = new MessagingWebSocket(httpServer);

  return httpServer;
}

server/storage.ts.content:
import {
  users,
  profiles,
  ratings,
  swipes,
  matches,
  miniChallenges,
  challengeResponses,
  matchConversations,
  messages,
  messageReactions,
  achievements,
  userAchievements,
  type User,
  type UpsertUser,
  type Profile,
  type InsertProfile,
  type Rating,
  type InsertRating,
  type Swipe,
  type InsertSwipe,
  type MiniChallenge,
  type InsertMiniChallenge,
  type ChallengeResponse,
  type InsertChallengeResponse,
  type MatchConversation,
  type InsertMatchConversation,
  type Message,
  type InsertMessage,
  type MessageReaction,
  type InsertMessageReaction,
  type Achievement,
  type InsertAchievement,
  type UserAchievement,
  type InsertUserAchievement,
  type ProfileWithUser,
} from "@shared/schema";
import { db } from "./db";
import { eq, and, ne, notInArray, desc, avg, count, inArray } from "drizzle-orm";

export interface IStorage {
  // User operations (mandatory for Replit Auth)
  getUser(id: string): Promise<User | undefined>;
  getUserByStripeCustomerId(stripeCustomerId: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;
  updateUser(userId: string, updates: Partial<UpsertUser>): Promise<User>;
  updateUserStripeInfo(userId: string, stripeCustomerId: string, stripeSubscriptionId: string): Promise<User>;

  // Profile operations
  getProfile(userId: string): Promise<Profile | undefined>;
  createProfile(profile: InsertProfile): Promise<Profile>;
  updateProfile(userId: string, profile: Partial<InsertProfile>): Promise<Profile>;

  // Discovery operations
  getDiscoverableProfiles(userId: string, limit?: number): Promise<ProfileWithUser[]>;
  getProfileWithDetails(userId: string): Promise<ProfileWithUser | undefined>;

  // Rating operations
  createRating(rating: InsertRating): Promise<Rating>;
  getRatingsForUser(userId: string): Promise<(Rating & { rater: User })[]>;
  getUserRatings(userId: string): Promise<Rating[]>;
  getAverageRating(userId: string): Promise<{ average: number; count: number }>;

  // Swipe operations
  createSwipe(swipe: InsertSwipe): Promise<Swipe>;
  getSwipesBetweenUsers(user1Id: string, user2Id: string): Promise<Swipe[]>;
  hasUserSwiped(swiperId: string, swipedUserId: string): Promise<boolean>;

  // Match operations
  createMatch(user1Id: string, user2Id: string): Promise<void>;
  getUserMatches(userId: string): Promise<ProfileWithUser[]>;

  // Mini-challenge operations
  getMiniChallenges(category?: string): Promise<MiniChallenge[]>;
  createChallengeResponse(response: InsertChallengeResponse): Promise<ChallengeResponse>;
  getUserChallengeResponses(userId: string): Promise<(ChallengeResponse & { challenge: MiniChallenge })[]>;
  getRandomChallenge(excludeAnswered?: string[]): Promise<MiniChallenge | null>;
  createMatchConversation(conversation: InsertMatchConversation): Promise<MatchConversation>;
  getMatchConversations(matchId: string): Promise<(MatchConversation & { challenge: MiniChallenge })[]>;

  // Messaging operations
  sendMessage(message: InsertMessage): Promise<Message>;
  getMessages(matchId: string, limit?: number): Promise<(Message & { sender: User; recipient: User })[]>;
  markMessageAsRead(messageId: number, userId: string): Promise<void>;
  getUnreadMessageCount(userId: string): Promise<number>;
  deleteMessage(messageId: number, userId: string): Promise<void>;

  // Message reactions
  addMessageReaction(reaction: InsertMessageReaction): Promise<MessageReaction>;
  removeMessageReaction(messageId: number, userId: string): Promise<void>;
  getMessageReactions(messageId: number): Promise<(MessageReaction & { user: User })[]>;

  // Achievement operations
  getAchievements(): Promise<Achievement[]>;
  createAchievement(achievement: InsertAchievement): Promise<Achievement>;
  getUserAchievements(userId: string): Promise<(UserAchievement & { achievement: Achievement })[]>;
  updateUserAchievementProgress(userId: string, achievementId: number, progress: number): Promise<UserAchievement>;
  unlockAchievement(userId: string, achievementId: number): Promise<UserAchievement>;
  getProfileCompletionScore(userId: string): Promise<{ score: number; maxScore: number; percentage: number }>;
  checkAndUpdateAchievements(userId: string): Promise<void>;
}

export class DatabaseStorage implements IStorage {
  // User operations
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async getUserByStripeCustomerId(stripeCustomerId: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.stripeCustomerId, stripeCustomerId));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  async updateUser(userId: string, updates: Partial<UpsertUser>): Promise<User> {
    const [user] = await db
      .update(users)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(users.id, userId))
      .returning();
    return user;
  }



  // Profile operations
  async getProfile(userId: string): Promise<Profile | undefined> {
    const [profile] = await db
      .select()
      .from(profiles)
      .where(eq(profiles.userId, userId));
    return profile;
  }

  async createProfile(profile: InsertProfile): Promise<Profile> {
    const [newProfile] = await db
      .insert(profiles)
      .values(profile)
      .returning();
    return newProfile;
  }

  async updateProfile(userId: string, profileData: Partial<InsertProfile>): Promise<Profile> {
    const [updatedProfile] = await db
      .update(profiles)
      .set({ ...profileData, updatedAt: new Date() })
      .where(eq(profiles.userId, userId))
      .returning();
    return updatedProfile;
  }

  // Discovery operations
  async getDiscoverableProfiles(userId: string, limit = 10): Promise<ProfileWithUser[]> {
    const userProfile = await this.getProfile(userId);
    if (!userProfile) return [];

    // Get users already swiped on
    const swipedUsers = await db
      .select({ swipedUserId: swipes.swipedUserId })
      .from(swipes)
      .where(eq(swipes.swiperId, userId));

    const swipedUserIds = swipedUsers.map(s => s.swipedUserId);

    // Get profiles of opposite gender that haven't been swiped on
    const profilesQuery = db
      .select()
      .from(profiles)
      .innerJoin(users, eq(profiles.userId, users.id))
      .where(
        and(
          ne(profiles.userId, userId),
          eq(profiles.gender, userProfile.gender === 'male' ? 'female' : 'male'),
          eq(profiles.isActive, true),
          swipedUserIds.length > 0 ? notInArray(profiles.userId, swipedUserIds) : undefined
        )
      )
      .limit(limit);

    const profilesWithUsers = await profilesQuery;

    // Get ratings for each profile
    const result: ProfileWithUser[] = [];
    for (const item of profilesWithUsers) {
      const avgRating = await this.getAverageRating(item.profiles.userId);
      const recentRatings = await db
        .select()
        .from(ratings)
        .innerJoin(users, eq(ratings.raterId, users.id))
        .where(eq(ratings.ratedUserId, item.profiles.userId))
        .orderBy(desc(ratings.createdAt))
        .limit(3);

      result.push({
        ...item.profiles,
        user: item.users,
        averageRating: avgRating.average,
        ratingCount: avgRating.count,
        recentRatings: recentRatings.map(r => ({ ...r.ratings, rater: r.users })),
        isOnline: Math.random() > 0.3, // Mock online status
      });
    }

    return result;
  }

  async getProfileWithDetails(userId: string): Promise<ProfileWithUser | undefined> {
    const [profileWithUser] = await db
      .select()
      .from(profiles)
      .innerJoin(users, eq(profiles.userId, users.id))
      .where(eq(profiles.userId, userId));

    if (!profileWithUser) return undefined;

    const avgRating = await this.getAverageRating(userId);
    const recentRatings = await db
      .select()
      .from(ratings)
      .innerJoin(users, eq(ratings.raterId, users.id))
      .where(eq(ratings.ratedUserId, userId))
      .orderBy(desc(ratings.createdAt))
      .limit(5);

    return {
      ...profileWithUser.profiles,
      user: profileWithUser.users,
      averageRating: avgRating.average,
      ratingCount: avgRating.count,
      recentRatings: recentRatings.map(r => ({ ...r.ratings, rater: r.users })),
      isOnline: Math.random() > 0.3, // Mock online status
    };
  }

  // Rating operations
  async createRating(rating: InsertRating): Promise<Rating> {
    const [newRating] = await db
      .insert(ratings)
      .values(rating)
      .returning();
    return newRating;
  }

  async getRatingsForUser(userId: string): Promise<(Rating & { rater: User })[]> {
    const result = await db
      .select()
      .from(ratings)
      .innerJoin(users, eq(ratings.raterId, users.id))
      .where(eq(ratings.ratedUserId, userId))
      .orderBy(desc(ratings.createdAt));

    return result.map(r => ({ ...r.ratings, rater: r.users }));
  }

  async getUserRatings(userId: string): Promise<Rating[]> {
    return await db
      .select()
      .from(ratings)
      .where(eq(ratings.raterId, userId))
      .orderBy(desc(ratings.createdAt));
  }

  async getAverageRating(userId: string): Promise<{ average: number; count: number }> {
    const [result] = await db
      .select({
        average: avg(ratings.score),
        count: count(),
      })
      .from(ratings)
      .where(eq(ratings.ratedUserId, userId));

    return {
      average: result.average ? parseFloat(result.average) : 0,
      count: result.count || 0,
    };
  }

  // Swipe operations
  async createSwipe(swipe: InsertSwipe): Promise<Swipe> {
    const [newSwipe] = await db
      .insert(swipes)
      .values(swipe)
      .returning();
    return newSwipe;
  }

  async getSwipesBetweenUsers(user1Id: string, user2Id: string): Promise<Swipe[]> {
    return await db
      .select()
      .from(swipes)
      .where(
        and(
          eq(swipes.swiperId, user1Id),
          eq(swipes.swipedUserId, user2Id)
        )
      );
  }

  async hasUserSwiped(swiperId: string, swipedUserId: string): Promise<boolean> {
    const [swipe] = await db
      .select()
      .from(swipes)
      .where(
        and(
          eq(swipes.swiperId, swiperId),
          eq(swipes.swipedUserId, swipedUserId)
        )
      );
    return !!swipe;
  }

  // Match operations
  async createMatch(user1Id: string, user2Id: string): Promise<void> {
    await db.insert(matches).values({
      user1Id,
      user2Id,
    });
  }

  async getUserMatches(userId: string): Promise<ProfileWithUser[]> {
    const matchesResult = await db
      .select()
      .from(matches)
      .where(
        and(
          eq(matches.user1Id, userId)
        )
      );

    const matchedUserIds = matchesResult.map(m => m.user2Id);
    if (matchedUserIds.length === 0) return [];

    const matchedProfiles = await db
      .select()
      .from(profiles)
      .innerJoin(users, eq(profiles.userId, users.id))
      .where(inArray(profiles.userId, matchedUserIds));

    const result: ProfileWithUser[] = [];
    for (const item of matchedProfiles) {
      const avgRating = await this.getAverageRating(item.profiles.userId);
      result.push({
        ...item.profiles,
        user: item.users,
        averageRating: avgRating.average,
        ratingCount: avgRating.count,
        isOnline: Math.random() > 0.3, // Mock online status
      });
    }

    return result;
  }

  async updateUserStripeInfo(userId: string, stripeCustomerId: string, stripeSubscriptionId: string): Promise<User> {
    const [user] = await db
      .update(users)
      .set({
        stripeCustomerId,
        stripeSubscriptionId,
        subscriptionStatus: 'active',
        updatedAt: new Date(),
      })
      .where(eq(users.id, userId))
      .returning();
    return user;
  }

  // Mini-challenge operations
  async getMiniChallenges(category?: string): Promise<MiniChallenge[]> {
    const query = db
      .select()
      .from(miniChallenges)
      .where(eq(miniChallenges.isActive, true));

    if (category) {
      query.where(eq(miniChallenges.category, category));
    }

    return await query;
  }

  async createChallengeResponse(response: InsertChallengeResponse): Promise<ChallengeResponse> {
    const [challengeResponse] = await db
      .insert(challengeResponses)
      .values(response)
      .returning();
    return challengeResponse;
  }

  async getUserChallengeResponses(userId: string): Promise<(ChallengeResponse & { challenge: MiniChallenge })[]> {
    return await db
      .select({
        id: challengeResponses.id,
        userId: challengeResponses.userId,
        challengeId: challengeResponses.challengeId,
        response: challengeResponses.response,
        createdAt: challengeResponses.createdAt,
        challenge: miniChallenges,
      })
      .from(challengeResponses)
      .innerJoin(miniChallenges, eq(challengeResponses.challengeId, miniChallenges.id))
      .where(eq(challengeResponses.userId, userId))
      .orderBy(desc(challengeResponses.createdAt));
  }

  async getRandomChallenge(excludeAnswered?: string[]): Promise<MiniChallenge | null> {
    let query = db
      .select()
      .from(miniChallenges)
      .where(eq(miniChallenges.isActive, true));

    if (excludeAnswered && excludeAnswered.length > 0) {
      query = query.where(notInArray(miniChallenges.id, excludeAnswered.map(id => parseInt(id))));
    }

    const challenges = await query;

    if (challenges.length === 0) return null;

    // Return a random challenge
    const randomIndex = Math.floor(Math.random() * challenges.length);
    return challenges[randomIndex];
  }

  async createMatchConversation(conversation: InsertMatchConversation): Promise<MatchConversation> {
    const [matchConversation] = await db
      .insert(matchConversations)
      .values(conversation)
      .returning();
    return matchConversation;
  }

  async getMatchConversations(matchId: string): Promise<(MatchConversation & { challenge: MiniChallenge })[]> {
    return await db
      .select({
        id: matchConversations.id,
        matchId: matchConversations.matchId,
        challengeId: matchConversations.challengeId,
        initiatorId: matchConversations.initiatorId,
        status: matchConversations.status,
        createdAt: matchConversations.createdAt,
        challenge: miniChallenges,
      })
      .from(matchConversations)
      .innerJoin(miniChallenges, eq(matchConversations.challengeId, miniChallenges.id))
      .where(eq(matchConversations.matchId, matchId))
      .orderBy(desc(matchConversations.createdAt));
  }

  // Achievement operations
  async getAchievements(): Promise<Achievement[]> {
    return await db
      .select()
      .from(achievements)
      .where(eq(achievements.isActive, true))
      .orderBy(achievements.category, achievements.points);
  }

  async createAchievement(achievement: InsertAchievement): Promise<Achievement> {
    const [result] = await db
      .insert(achievements)
      .values(achievement)
      .returning();
    return result;
  }

  async getUserAchievements(userId: string): Promise<(UserAchievement & { achievement: Achievement })[]> {
    return await db
      .select({
        id: userAchievements.id,
        userId: userAchievements.userId,
        achievementId: userAchievements.achievementId,
        unlockedAt: userAchievements.unlockedAt,
        progress: userAchievements.progress,
        maxProgress: userAchievements.maxProgress,
        isCompleted: userAchievements.isCompleted,
        achievement: {
          id: achievements.id,
          key: achievements.key,
          title: achievements.title,
          description: achievements.description,
          icon: achievements.icon,
          category: achievements.category,
          points: achievements.points,
          condition: achievements.condition,
          isActive: achievements.isActive,
          createdAt: achievements.createdAt,
        },
      })
      .from(userAchievements)
      .innerJoin(achievements, eq(userAchievements.achievementId, achievements.id))
      .where(eq(userAchievements.userId, userId))
      .orderBy(desc(userAchievements.unlockedAt));
  }

  async updateUserAchievementProgress(userId: string, achievementId: number, progress: number): Promise<UserAchievement> {
    const [existing] = await db
      .select()
      .from(userAchievements)
      .where(and(
        eq(userAchievements.userId, userId),
        eq(userAchievements.achievementId, achievementId)
      ));

    if (existing) {
      const [updated] = await db
        .update(userAchievements)
        .set({
          progress,
          isCompleted: progress >= existing.maxProgress
        })
        .where(eq(userAchievements.id, existing.id))
        .returning();
      return updated;
    } else {
      const [created] = await db
        .insert(userAchievements)
        .values({
          userId,
          achievementId,
          progress,
          maxProgress: 1,
          isCompleted: progress >= 1
        })
        .returning();
      return created;
    }
  }

  async unlockAchievement(userId: string, achievementId: number): Promise<UserAchievement> {
    const [existing] = await db
      .select()
      .from(userAchievements)
      .where(and(
        eq(userAchievements.userId, userId),
        eq(userAchievements.achievementId, achievementId)
      ));

    if (existing && !existing.isCompleted) {
      const [updated] = await db
        .update(userAchievements)
        .set({
          progress: existing.maxProgress,
          isCompleted: true,
          unlockedAt: new Date()
        })
        .where(eq(userAchievements.id, existing.id))
        .returning();
      return updated;
    } else if (!existing) {
      const [created] = await db
        .insert(userAchievements)
        .values({
          userId,
          achievementId,
          progress: 1,
          maxProgress: 1,
          isCompleted: true
        })
        .returning();
      return created;
    }

    return existing;
  }

  async getProfileCompletionScore(userId: string): Promise<{ score: number; maxScore: number; percentage: number }> {
    const [user] = await db
      .select()
      .from(users)
      .where(eq(users.id, userId));

    const [profile] = await db
      .select()
      .from(profiles)
      .where(eq(profiles.userId, userId));

    if (!user) {
      return { score: 0, maxScore: 100, percentage: 0 };
    }

    let score = 0;
    const maxScore = 100;

    // Basic info (30 points)
    if (user.email) score += 10;
    if (user.firstName) score += 10;
    if (user.profileImageUrl) score += 10;

    // Profile info (70 points)
    if (profile) {
      if (profile.age && profile.age > 0) score += 15;
      if (profile.gender) score += 15;
      if (profile.bio && profile.bio.trim().length > 20) score += 20;
      if (profile.location) score += 10;
      if (profile.occupation) score += 10;
    }

    const percentage = Math.round((score / maxScore) * 100);
    return { score, maxScore, percentage };
  }

  async checkAndUpdateAchievements(userId: string): Promise<void> {
    const allAchievements = await this.getAchievements();
    const userAchievements = await this.getUserAchievements(userId);
    const completedAchievementIds = new Set(
      userAchievements.filter(ua => ua.isCompleted).map(ua => ua.achievementId)
    );

    for (const achievement of allAchievements) {
      if (completedAchievementIds.has(achievement.id)) continue;

      const condition = achievement.condition as any;
      let shouldUnlock = false;

      switch (achievement.key) {
        case 'profile_complete':
          const completion = await this.getProfileCompletionScore(userId);
          shouldUnlock = completion.percentage >= 100;
          break;

        case 'first_photo':
          const [user] = await db.select().from(users).where(eq(users.id, userId));
          shouldUnlock = !!user?.profileImageUrl;
          break;

        case 'bio_writer':
          const [profile] = await db.select().from(profiles).where(eq(profiles.userId, userId));
          shouldUnlock = !!(profile?.bio && profile.bio.trim().length > 20);
          break;

        case 'challenge_participant':
          const responses = await db
            .select({ count: count() })
            .from(challengeResponses)
            .where(eq(challengeResponses.userId, userId));
          shouldUnlock = responses[0]?.count >= 1;
          break;

        case 'social_butterfly':
          const challengeCount = await db
            .select({ count: count() })
            .from(challengeResponses)
            .where(eq(challengeResponses.userId, userId));
          shouldUnlock = challengeCount[0]?.count >= 5;
          break;

        case 'honest_reviewer':
          const ratingsGiven = await db
            .select({ count: count() })
            .from(ratings)
            .where(eq(ratings.raterId, userId));
          shouldUnlock = ratingsGiven[0]?.count >= 3;
          break;
      }

      if (shouldUnlock) {
        await this.unlockAchievement(userId, achievement.id);
      }
    }
  }

  // Messaging operations
  async sendMessage(message: InsertMessage): Promise<Message> {
    const [sentMessage] = await db
      .insert(messages)
      .values(message)
      .returning();
    return sentMessage;
  }

  async getMessages(matchId: string, limit = 50): Promise<(Message & { sender: User; recipient: User })[]> {
    const messagesWithSender = await db
      .select({
        id: messages.id,
        matchId: messages.matchId,
        senderId: messages.senderId,
        recipientId: messages.recipientId,
        content: messages.content,
        messageType: messages.messageType,
        isRead: messages.isRead,
        readAt: messages.readAt,
        createdAt: messages.createdAt,
        sender: users,
      })
      .from(messages)
      .innerJoin(users, eq(messages.senderId, users.id))
      .where(eq(messages.matchId, matchId))
      .orderBy(desc(messages.createdAt))
      .limit(limit);

    // Get recipients for each message
    const recipientIds = [...new Set(messagesWithSender.map(m => m.recipientId))];
    const recipients = await db
      .select()
      .from(users)
      .where(inArray(users.id, recipientIds));

    // Map recipients to messages
    const recipientMap = new Map(recipients.map(r => [r.id, r]));

    return messagesWithSender.map(msg => ({
      ...msg,
      recipient: recipientMap.get(msg.recipientId)!,
    }));
  }

  async markMessageAsRead(messageId: number, userId: string): Promise<void> {
    await db
      .update(messages)
      .set({ 
        isRead: true, 
        readAt: new Date() 
      })
      .where(
        and(
          eq(messages.id, messageId),
          eq(messages.recipientId, userId)
        )
      );
  }

  async getUnreadMessageCount(userId: string): Promise<number> {
    const result = await db
      .select({ count: count() })
      .from(messages)
      .where(
        and(
          eq(messages.recipientId, userId),
          eq(messages.isRead, false)
        )
      );
    return result[0]?.count || 0;
  }

  async deleteMessage(messageId: number, userId: string): Promise<void> {
    await db
      .delete(messages)
      .where(
        and(
          eq(messages.id, messageId),
          eq(messages.senderId, userId)
        )
      );
  }

  // Message reactions
  async addMessageReaction(reaction: InsertMessageReaction): Promise<MessageReaction> {
    const [messageReaction] = await db
      .insert(messageReactions)
      .values(reaction)
      .returning();
    return messageReaction;
  }

  async removeMessageReaction(messageId: number, userId: string): Promise<void> {
    await db
      .delete(messageReactions)
      .where(
        and(
          eq(messageReactions.messageId, messageId),
          eq(messageReactions.userId, userId)
        )
      );
  }

  async getMessageReactions(messageId: number): Promise<(MessageReaction & { user: User })[]> {
    return await db
      .select({
        id: messageReactions.id,
        messageId: messageReactions.messageId,
        userId: messageReactions.userId,
        reactionType: messageReactions.reactionType,
        createdAt: messageReactions.createdAt,
        user: users,
      })
      .from(messageReactions)
      .innerJoin(users, eq(messageReactions.userId, users.id))
      .where(eq(messageReactions.messageId, messageId))
      .orderBy(desc(messageReactions.createdAt));
  }
}

