  --secondary-foreground: hsl(0, 0%, 100%);
  --accent: hsl(45, 100%, 51%);
  --accent-foreground: hsl(26, 83%, 14%);
  --destructive: hsl(0, 62.8%, 30.6%);
  --destructive-foreground: hsl(0, 0%, 98%);
  --ring: hsl(240, 4.9%, 83.9%);
  --radius: 0.5rem;
  --success: hsl(122, 39%, 49%);
  --trust: hsl(207, 90%, 54%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground;
  }
}

@layer components {
  .gradient-primary {
    background: linear-gradient(135deg, hsl(339, 82%, 52%) 0%, hsl(207, 90%, 54%) 100%);
  }

  .gradient-trust {
    background: linear-gradient(135deg, hsl(207, 90%, 54%) 0%, hsl(204, 94%, 54%) 100%);
  }
}

