import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Icons } from "@/lib/icons";
import { formatDistanceToNow } from "@/lib/dateUtils";
import type { ChallengeResponse, MiniChallenge } from "@shared/schema";
import { useAuth, authenticatedFetch } from "@/hooks/useAuth";


interface ChallengeResponsesListProps {
  userId?: string;
  maxHeight?: string;
  onNewChallenge?: () => void;
}

export function ChallengeResponsesList({ 
  userId, 
  maxHeight = "400px",
  onNewChallenge 
}: ChallengeResponsesListProps) {
  const { isAuthenticated, isLoading: authLoading } = useAuth();

  const { data: responses = [], isLoading, refetch } = useQuery({
    queryKey: ["/api/my-challenge-responses"],
    queryFn: async () => {
      const response = await authenticatedFetch("/api/my-challenge-responses");
      if (!response.ok) {
        throw new Error('Failed to fetch challenge responses');
      }
      return response.json();
    },
    enabled: isAuthenticated && !authLoading,
  });

  const getCategoryIcon = (category: string) => {
    switch (category) {
      case 'this_or_that':
        return <Icons.Heart />;
      case 'quick_question':
        return <Icons.MessageCircle />;
      case 'fun_fact':
        return <Icons.Lightbulb />;
      case 'preference':
        return <Icons.Star />;
      default:
        return <Icons.Coffee className="w-4 h-4" />;
    }
  };

  const getCategoryLabel = (category: string) => {
    switch (category) {
      case 'this_or_that':
        return "Ceci ou Cela";
      case 'quick_question':
        return "Question Rapide";
      case 'fun_fact':
        return "Fait Amusant";
      case 'preference':
        return "Pr√©f√©rence";
      default:
        return "D√©fi";
    }
  };

  const getCategoryColor = (category: string) => {
    switch (category) {
      case 'this_or_that':
        return "bg-pink-100 text-pink-700 border-pink-200";
      case 'quick_question':
        return "bg-blue-100 text-blue-700 border-blue-200";
      case 'fun_fact':
        return "bg-yellow-100 text-yellow-700 border-yellow-200";
      case 'preference':
        return "bg-purple-100 text-purple-700 border-purple-200";
      default:
        return "bg-gray-100 text-gray-700 border-gray-200";
    }
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center py-8">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-pink-500"></div>
      </div>
    );
  }

  return (
    <Card>
      <CardHeader>
        <div className="flex items-center justify-between">
          <CardTitle className="flex items-center space-x-2">
            <Icons.MessageCircle className="w-5 h-5" />
            <span>Mes R√©ponses aux D√©fis</span>
            <Badge variant="outline" className="ml-2">
              {responses.length}
            </Badge>
          </CardTitle>
          <div className="flex space-x-2">
            <Button
              variant="outline"
              size="sm"
              onClick={() => refetch()}
              className="flex items-center space-x-1"
            >
              <Icons.RefreshCw className="w-4 h-4" />
            </Button>
            {onNewChallenge && (
              <Button
                variant="outline"
                size="sm"
                onClick={onNewChallenge}
                className="flex items-center space-x-1"
              >
                <Icons.Star className="w-4 h-4" />
                <span>Nouveau d√©fi</span>
              </Button>
            )}
          </div>
        </div>
      </CardHeader>

      <CardContent>
        {responses.length === 0 ? (
          <div className="text-center py-8 space-y-4">
            <Icons.Coffee className="w-16 h-16 text-gray-400 mx-auto" />
            <p className="text-gray-600">
              Vous n'avez pas encore r√©pondu √† des d√©fis
            </p>
            <p className="text-sm text-gray-500">
              Participez aux mini-d√©fis pour am√©liorer vos conversations !
            </p>
            {onNewChallenge && (
              <Button onClick={onNewChallenge} className="mt-4">
                <Icons.Star className="w-4 h-4 mr-2" />
                Commencer un d√©fi
              </Button>
            )}
          </div>
        ) : (
          <ScrollArea className={`w-full pr-4`} style={{ maxHeight }}>
            <div className="space-y-4">
              {responses.map((response: ChallengeResponse & { challenge: MiniChallenge }) => (
                <Card key={response.id} className="shadow-sm">
                  <CardContent className="p-4">
                    <div className="flex items-start justify-between mb-3">
                      <Badge 
                        variant="outline" 
                        className={getCategoryColor(response.challenge.category)}
                      >
                        {getCategoryIcon(response.challenge.category)}
                        <span className="ml-1">{getCategoryLabel(response.challenge.category)}</span>
                      </Badge>
                      <div className="flex items-center space-x-1 text-sm text-gray-500">
                        <Icons.Clock className="w-3 h-3" />
                        <span>
                          {formatDistanceToNow(new Date(response.createdAt))}
                        </span>
                      </div>
                    </div>

                    <h4 className="font-semibold text-gray-800 mb-2">
                      {response.challenge.title}
                    </h4>

                    <p className="text-sm text-gray-600 mb-3">
                      {response.challenge.description}
                    </p>

                    <div className="bg-gray-50 rounded-lg p-3">
                      <p className="text-sm font-medium text-gray-700">
                        Ma r√©ponse:
                      </p>
                      <p className="text-gray-800 mt-1">
                        {response.response}
                      </p>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          </ScrollArea>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/HonestyMeter.tsx.content:
import { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { Icons } from "@/lib/icons";

interface HonestyMeterProps {
  rating: number;
  ratingCount: number;
  size?: "sm" | "md" | "lg";
  animated?: boolean;
  showLabel?: boolean;
}

export function HonestyMeter({ 
  rating, 
  ratingCount, 
  size = "md", 
  animated = true, 
  showLabel = true 
}: HonestyMeterProps) {
  const [animatedRating, setAnimatedRating] = useState(0);
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    setIsVisible(true);
    const timer = setTimeout(() => {
      setAnimatedRating(rating);
    }, 300);
    return () => clearTimeout(timer);
  }, [rating]);

  const getHonestyLevel = (rating: number) => {
    if (rating >= 4.5) return { label: "Tr√®s Honn√™te", color: "from-green-400 to-emerald-500", icon: Icons.Shield };
    if (rating >= 4) return { label: "Honn√™te", color: "from-blue-400 to-cyan-500", icon: Icons.Star };
    if (rating >= 3.5) return { label: "Assez Honn√™te", color: "from-yellow-400 to-orange-500", icon: Icons.Heart };
    if (rating >= 3) return { label: "Moyennement Honn√™te", color: "from-orange-400 to-red-500", icon: Icons.Sparkles };
    return { label: "Peu Honn√™te", color: "from-red-400 to-pink-500", icon: Icons.Sparkles };
  };

  const honestyLevel = getHonestyLevel(rating);
  const percentage = (rating / 5) * 100;

  const sizeClasses = {
    sm: { 
      container: "w-24 h-24", 
      text: "text-xs", 
      icon: "w-4 h-4",
      meter: "w-20 h-20",
      strokeWidth: 6
    },
    md: { 
      container: "w-32 h-32", 
      text: "text-sm", 
      icon: "w-5 h-5",
      meter: "w-28 h-28",
      strokeWidth: 8
    },
    lg: { 
      container: "w-40 h-40", 
      text: "text-base", 
      icon: "w-6 h-6",
      meter: "w-36 h-36",
      strokeWidth: 10
    }
  };

  const classes = sizeClasses[size];
  const radius = 50;
  const circumference = 2 * Math.PI * radius;
  const strokeDasharray = circumference;
  const strokeDashoffset = circumference - (animatedRating / 5) * circumference;

  return (
    <div className={`relative ${classes.container} mx-auto`}>
      <AnimatePresence>
        {isVisible && (
          <motion.div
            initial={{ scale: 0, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            exit={{ scale: 0, opacity: 0 }}
            transition={{ duration: 0.5, ease: "easeOut" }}
            className="relative w-full h-full"
          >
            {/* Background Circle */}
            <svg 
              className="absolute inset-0 w-full h-full transform -rotate-90"
              viewBox="0 0 120 120"
            >
              <circle
                cx="60"
                cy="60"
                r={radius}
                fill="none"
                stroke="currentColor"
                strokeWidth={classes.strokeWidth}
                className="text-gray-200"
              />

              {/* Animated Progress Circle */}
              <motion.circle
                cx="60"
                cy="60"
                r={radius}
                fill="none"
                strokeWidth={classes.strokeWidth}
                strokeLinecap="round"
                className={`bg-gradient-to-r ${honestyLevel.color}`}
                style={{
                  strokeDasharray,
                  strokeDashoffset: animated ? strokeDashoffset : circumference - (rating / 5) * circumference,
                }}
                initial={animated ? { strokeDashoffset: circumference } : undefined}
                animate={animated ? { strokeDashoffset } : undefined}
                transition={{ duration: 1.5, ease: "easeInOut", delay: 0.3 }}
                stroke="url(#gradient)"
              />

              {/* Gradient Definition */}
              <defs>
                <linearGradient id="gradient" x1="0%" y1="0%" x2="100%" y2="100%">
                  <stop offset="0%" className={honestyLevel.color.split(' ')[0].replace('from-', 'stop-')} />
                  <stop offset="100%" className={honestyLevel.color.split(' ')[2].replace('to-', 'stop-')} />
                </linearGradient>
              </defs>
            </svg>

            {/* Center Content */}
            <div className="absolute inset-0 flex flex-col items-center justify-center">
              <motion.div
                initial={{ scale: 0 }}
                animate={{ scale: 1 }}
                transition={{ duration: 0.5, delay: 0.8 }}
                className={`p-2 rounded-full bg-gradient-to-r ${honestyLevel.color} shadow-lg`}
              >
                <honestyLevel.icon className={`${classes.icon} text-white`} />
              </motion.div>

              <motion.div
                initial={{ opacity: 0, y: 10 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: 1 }}
                className="text-center mt-1"
              >
                <div className={`font-bold text-gray-800 ${classes.text}`}>
                  {rating.toFixed(1)}
                </div>
                <div className={`text-gray-500 ${classes.text}`}>
                  {ratingCount} avis
                </div>
              </motion.div>
            </div>

            {/* Floating Particles */}
            {animated && rating >= 4 && (
              <div className="absolute inset-0 pointer-events-none">
                {[...Array(3)].map((_, i) => (
                  <motion.div
                    key={i}
                    initial={{ opacity: 0, scale: 0, x: 0, y: 0 }}
                    animate={{ 
                      opacity: [0, 1, 0],
                      scale: [0, 1, 0],
                      x: [0, (i - 1) * 20, (i - 1) * 40],
                      y: [0, -20, -40]
                    }}
                    transition={{
                      duration: 2,
                      delay: 1.5 + i * 0.3,
                      repeat: Infinity,
                      repeatDelay: 3
                    }}
                    className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2"
                  >
                    <Sparkles className="w-3 h-3 text-yellow-400" />
                  </motion.div>
                ))}
              </div>
            )}
          </motion.div>
        )}
      </AnimatePresence>

      {/* Honesty Level Label */}
      {showLabel && (
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5, delay: 1.2 }}
          className="absolute -bottom-8 left-1/2 transform -translate-x-1/2 text-center"
        >
          <div className={`font-medium text-gray-700 ${classes.text}`}>
            {honestyLevel.label}
          </div>
        </motion.div>
      )}
    </div>
  );
}

client/src/components/HonestyMeterCard.t.content:
import { motion } from "framer-motion";
import { HonestyMeter } from "./HonestyMeter";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Icons } from "@/lib/icons";

interface HonestyMeterCardProps {
  rating: number;
  ratingCount: number;
  previousRating?: number;
  recentRatings?: any[];
  showTrend?: boolean;
  compact?: boolean;
}

export function HonestyMeterCard({ 
  rating, 
  ratingCount, 
  previousRating, 
  recentRatings = [],
  showTrend = true,
  compact = false
}: HonestyMeterCardProps) {
  const getTrend = () => {
    if (!previousRating || previousRating === 0) return null;
    const diff = rating - previousRating;
    if (Math.abs(diff) < 0.1) return { type: 'stable', value: 0 };
    return {
      type: diff > 0 ? 'up' : 'down',
      value: Math.abs(diff)
    };
  };

  const trend = getTrend();

  const getTrendIcon = () => {
    if (!trend) return null;
    switch (trend.type) {
      case 'up':
        return <Icons.TrendingUp className="w-4 h-4 text-green-500" />;
      case 'down':
        return <Icons.TrendingDown className="w-4 h-4 text-red-500" />;
      default:
        return <Icons.Minus className="w-4 h-4 text-gray-500" />;
    }
  };

  const getTrendColor = () => {
    if (!trend) return 'text-gray-600';
    switch (trend.type) {
      case 'up':
        return 'text-green-600';
      case 'down':
        return 'text-red-600';
      default:
        return 'text-gray-600';
    }
  };

  const getRecentTrend = () => {
    if (recentRatings.length < 2) return null;
    const recent = recentRatings.slice(0, 3);
    const older = recentRatings.slice(3, 6);

    if (older.length === 0) return null;

    const recentAvg = recent.reduce((sum, r) => sum + parseFloat(r.score), 0) / recent.length;
    const olderAvg = older.reduce((sum, r) => sum + parseFloat(r.score), 0) / older.length;

    return recentAvg - olderAvg;
  };

  if (compact) {
    return (
      <Card className="bg-gradient-to-br from-white to-gray-50 border border-gray-200 shadow-sm">
        <CardContent className="p-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <HonestyMeter 
                rating={rating}
                ratingCount={ratingCount}
                size="sm"
                animated={true}
                showLabel={false}
              />
              <div>
                <div className="text-sm font-medium text-gray-800">
                  Score d'Honn√™tet√©
                </div>
                <div className="flex items-center space-x-2">
                  <span className="text-lg font-bold text-blue-600">
                    {rating.toFixed(1)}
                  </span>
                  <span className="text-sm text-gray-500">
                    ({ratingCount} avis)
                  </span>
                </div>
              </div>
            </div>

            {showTrend && trend && (
              <div className="flex items-center space-x-1">
                {getTrendIcon()}
                <span className={`text-sm font-medium ${getTrendColor()}`}>
                  {trend.value > 0 ? `+${trend.value.toFixed(1)}` : trend.value.toFixed(1)}
                </span>
              </div>
            )}
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className="bg-gradient-to-br from-white via-blue-50 to-purple-50 border border-gray-200 shadow-lg">
      <CardContent className="p-6">
        <div className="text-center space-y-4">
          <div className="flex items-center justify-center space-x-2">
            <Icons.Star className="w-5 h-5 text-yellow-500" />
            <h3 className="text-lg font-semibold text-gray-800">
              Score d'Honn√™tet√©
            </h3>
          </div>

          <motion.div
            initial={{ scale: 0.8, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            transition={{ duration: 0.5 }}
          >
            <HonestyMeter 
              rating={rating}
              ratingCount={ratingCount}
              size="lg"
              animated={true}
              showLabel={true}
            />
          </motion.div>

          {/* Statistics */}
          <div className="grid grid-cols-2 gap-4 pt-4">
            <motion.div
              initial={{ y: 20, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ delay: 0.3 }}
              className="text-center"
            >
              <div className="text-2xl font-bold text-blue-600">
                {rating.toFixed(1)}
              </div>
              <div className="text-sm text-gray-600">Note moyenne</div>
            </motion.div>

            <motion.div
              initial={{ y: 20, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ delay: 0.4 }}
              className="text-center"
            >
