                {trend ? (
                  <>
                    {getTrendIcon()}
                    <span className={`text-sm font-medium ${getTrendColor()}`}>
                      {trend.type === 'up' ? 'En hausse' : 
                       trend.type === 'down' ? 'En baisse' : 'Stable'}
                    </span>
                    {trend.value > 0 && (
                      <Badge variant="outline" className={getTrendColor()}>
                        {trend.type === 'up' ? '+' : '-'}{trend.value.toFixed(1)}
                      </Badge>
                    )}
                  </>
                ) : (
                  <span className="text-sm text-gray-500">
                    Pas assez de donn√©es pour la tendance
                  </span>
                )}
              </div>
            </motion.div>
          )}

          {/* Recent Activity */}
          {recentRatings.length > 0 && (
            <motion.div
              initial={{ y: 20, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ delay: 0.6 }}
              className="pt-4 border-t border-gray-200"
            >
              <div className="flex items-center justify-center space-x-2 mb-3">
                <Users className="w-4 h-4 text-gray-500" />
                <span className="text-sm text-gray-600">Activit√© r√©cente</span>
              </div>
              <div className="flex justify-center space-x-1">
                {recentRatings.slice(0, 5).map((rating, index) => (
                  <motion.div
                    key={index}
                    initial={{ scale: 0 }}
                    animate={{ scale: 1 }}
                    transition={{ delay: 0.7 + index * 0.1 }}
                    className="flex items-center space-x-1"
                  >
                    <div className="flex">
                      {Array.from({ length: 5 }, (_, i) => (
                        <Star
                          key={i}
