import { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Icons } from "@/lib/icons";
import { formatDate } from "@/lib/dateUtils";
import { HonestyMeter } from "./HonestyMeter";
import type { ProfileWithUser } from "@shared/schema";

interface ProfileCardProps {
  profile: ProfileWithUser;
  onRate: () => void;
  canRate: boolean;
}

export function ProfileCard({ profile, onRate, canRate }: ProfileCardProps) {
  const { toast } = useToast();
  const [imageError, setImageError] = useState(false);

  const swipeMutation = useMutation({
    mutationFn: async ({ liked }: { liked: boolean }) => {
      await apiRequest('POST', '/api/swipes', {
        swipedUserId: profile.userId,
        liked,
      });
    },
    onSuccess: (data: any) => {
      if (data.matched) {
        toast({
          title: "It's a match!",
          description: "You both liked each other!",
        });
      }
      queryClient.invalidateQueries({ queryKey: ["/api/discover"] });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: "Failed to swipe",
        variant: "destructive",
      });
    },
  });

  const handleSwipe = (liked: boolean) => {
    swipeMutation.mutate({ liked });
  };

  const renderStars = (rating: number) => {
    return Array.from({ length: 5 }, (_, i) => (
      <Icons.Star
        key={i}
        className={`w-4 h-4 ${i < Math.floor(rating) ? 'text-yellow-400 fill-current' : 'text-gray-300'}`}
      />
    ));
  };

  const profileImageUrl = profile.user.profileImageUrl || `https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=800&h=600`;

  return (
    <Card className="bg-white rounded-2xl shadow-lg border border-gray-100 overflow-hidden">
      {/* Profile Image */}
      <div className="relative">
        <img 
          src={imageError ? 'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=800&h=600' : profileImageUrl}
          alt={`Profile de ${profile.user.firstName}`}
          className="w-full h-80 object-cover"
          onError={() => setImageError(true)}
        />

        {/* Honesty Meter */}
        <div className="absolute top-4 right-4">
          <HonestyMeter 
            rating={profile.averageRating || 0}
            ratingCount={profile.ratingCount || 0}
            size="sm"
            animated={true}
            showLabel={false}
          />
        </div>

        {/* Online Status */}
        <div className="absolute bottom-4 left-4 flex items-center space-x-2">
          <div className={`w-3 h-3 rounded-full ${profile.isOnline ? 'bg-green-500' : 'bg-gray-400'}`}></div>
          <span className="text-white text-sm font-medium bg-black bg-opacity-50 rounded-full px-2 py-1">
            {profile.isOnline ? 'En ligne' : 'Il y a 2h'}
          </span>
        </div>
      </div>

      {/* Profile Info */}
      <div className="p-4">
        <div className="flex items-center justify-between mb-3">
          <div>
            <h3 className="text-xl font-bold text-gray-800">
              {profile.user.firstName || 'Utilisateur'}
            </h3>
            <div className="flex items-center space-x-2 text-gray-600 text-sm">
              <span>{profile.age} ans</span>
              {profile.occupation && (
                <>
                  <span>‚Ä¢</span>
                  <div className="flex items-center space-x-1">
                    <Icons.Briefcase className="w-3 h-3" />
                    <span>{profile.occupation}</span>
                  </div>
                </>
              )}
              {profile.location && (
                <>
                  <span>‚Ä¢</span>
                  <div className="flex items-center space-x-1">
                    <Icons.MapPin className="w-3 h-3" />
                    <span>{profile.location}</span>
                  </div>
                </>
              )}
            </div>
          </div>
          <div className="flex justify-end">
            <HonestyMeter 
              rating={profile.averageRating || 0}
              ratingCount={profile.ratingCount || 0}
              size="md"
              animated={true}
              showLabel={false}
            />
          </div>
        </div>

        {/* Trust Indicators */}
        <div className="flex items-center space-x-4 mb-4">
          <div className="flex items-center space-x-1">
            <Shield className="w-4 h-4 text-green-500" />
            <span className="text-xs text-gray-600">Profil v√©rifi√©</span>
          </div>
          {profile.isOnline && (
            <div className="flex items-center space-x-1">
              <Icons.Clock className="w-4 h-4 text-blue-500" />
              <span className="text-xs text-gray-600">R√©ponse rapide</span>
            </div>
          )}
          {(profile.averageRating || 0) >= 4.5 && (
            <div className="flex items-center space-x-1">
              <Icons.Star className="w-4 h-4 text-yellow-500" />
              <span className="text-xs text-gray-600">Top √©valu√©</span>
            </div>
          )}
        </div>

        {/* Bio */}
        {profile.bio && (
          <div className="mb-4">
            <p className="text-sm text-gray-700 line-clamp-3">{profile.bio}</p>
          </div>
        )}

        {/* Recent Reviews Preview */}
        {profile.recentRatings && profile.recentRatings.length > 0 && (
          <div className="mb-4">
            <p className="text-xs text-gray-500 mb-2">Derniers avis:</p>
            <div className="space-y-2">
              {profile.recentRatings.slice(0, 2).map((rating, index) => (
                <div key={index} className="bg-gray-50 rounded-lg p-2">
                  <div className="flex items-center space-x-2 mb-1">
                    <div className="flex">
                      {renderStars(parseFloat(rating.score))}
                    </div>
                    <span className="text-xs text-gray-500">
                      {rating.rater.firstName || 'Anonyme'}, {formatDate(new Date(rating.createdAt), 'dd/MM/yyyy')}
                    </span>
                  </div>
                  {rating.comment && (
                    <p className="text-xs text-gray-700 line-clamp-2">{rating.comment}</p>
                  )}
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Action Buttons */}
        <div className="flex space-x-3">
          <Button 
            variant="outline"
            className="flex-1 py-3 rounded-xl font-medium hover:bg-gray-50"
            onClick={() => handleSwipe(false)}
            disabled={swipeMutation.isPending}
          >
            <Icons.X className="w-4 h-4 mr-2" />
            Passer
          </Button>
          <Button 
            className="flex-1 gradient-primary text-white py-3 rounded-xl font-medium hover:opacity-90"
            onClick={() => handleSwipe(true)}
            disabled={swipeMutation.isPending}
          >
            <Icons.Heart className="w-4 h-4 mr-2" />
            Int√©ress√©e
          </Button>
        </div>

        {/* Rating Button for Women */}
        {canRate && (
          <Button 
            variant="outline" 
            className="w-full mt-3 py-3 rounded-xl font-medium border-blue-200 text-blue-600 hover:bg-blue-50"
            onClick={onRate}
          >
            <Icons.Star className="w-4 h-4 mr-2" />
            √âvaluer l'honn√™tet√©
          </Button>
        )}
      </div>
    </Card>
  );
}

client/src/components/ProfileCompletionC.content:
import { useQuery } from "@tanstack/react-query";
import { useAuth, authenticatedFetch } from "@/hooks/useAuth";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Progress } from "@/components/ui/progress";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { 
  User, 
  CheckCircle, 
  AlertCircle, 
  TrendingUp, 
  Target,
  Sparkles
} from "lucide-react";
import { cn } from "@/lib/utils";

interface ProfileCompletionCardProps {
  onImprove?: () => void;
  compact?: boolean;
}

export function ProfileCompletionCard({ onImprove, compact = false }: ProfileCompletionCardProps) {
  // Add the missing useAuth hook
  const { isAuthenticated, isLoading: authLoading } = useAuth();

  const { data: completion, isLoading } = useQuery({
    queryKey: ["/api/profile-completion"],
    queryFn: async () => {
      const response = await authenticatedFetch("/api/profile-completion");
      if (!response.ok) {
        throw new Error('Failed to fetch profile completion');
      }
      return response.json();
    },
    enabled: isAuthenticated && !authLoading,
  });

  if (isLoading || authLoading) {
    return (
      <Card className="animate-pulse">
        <CardContent className="p-6">
          <div className="h-4 bg-gray-200 rounded w-3/4 mb-4"></div>
          <div className="h-8 bg-gray-200 rounded mb-2"></div>
          <div className="h-4 bg-gray-200 rounded w-1/2"></div>
        </CardContent>
      </Card>
    );
  }

  if (!completion) return null;

  const { percentage, score, maxScore } = completion;
  const isComplete = percentage >= 100;
  const isNearComplete = percentage >= 80;

  const getStatusColor = () => {
    if (isComplete) return "text-green-600";
    if (isNearComplete) return "text-yellow-600";
    return "text-red-600";
  };

  const getStatusIcon = () => {
    if (isComplete) return <CheckCircle className="w-5 h-5 text-green-600" />;
    if (isNearComplete) return <AlertCircle className="w-5 h-5 text-yellow-600" />;
    return <Target className="w-5 h-5 text-red-600" />;
  };

  const getStatusMessage = () => {
    if (isComplete) return "Profil termin√© !";
    if (isNearComplete) return "Presque termin√©";
    return "Profil incomplet";
  };

  const getMotivationalMessage = () => {
    if (isComplete) return "Votre profil est parfait ! Vous attirez plus de matches.";
    if (percentage >= 60) return "Encore quelques d√©tails et votre profil sera parfait !";
    if (percentage >= 40) return "Vous √™tes sur la bonne voie, continuez !";
    return "Compl√©tez votre profil pour obtenir plus de matches !";
  };

  const getProgressColor = () => {
    if (isComplete) return "from-green-500 to-emerald-500";
    if (isNearComplete) return "from-yellow-500 to-orange-500";
    return "from-red-500 to-pink-500";
  };

  if (compact) {
    return (
      <Card className="border-l-4 border-l-pink-500">
        <CardContent className="p-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              {getStatusIcon()}
              <div>
                <h3 className="font-semibold text-sm">{getStatusMessage()}</h3>
                <p className="text-xs text-gray-600">{percentage}% termin√©</p>
              </div>
            </div>
            <div className="text-right">
              <div className="text-lg font-bold text-pink-600">{percentage}%</div>
              <Badge variant="outline" className="text-xs">
                {score}/{maxScore}
              </Badge>
            </div>
          </div>
          <Progress 
            value={percentage} 
            className="mt-3 h-2"
            style={{
              background: `linear-gradient(to right, ${getProgressColor()})`
            }}
          />
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className={cn(
      "relative overflow-hidden",
      isComplete && "border-2 border-green-200 bg-gradient-to-br from-green-50 to-emerald-50"
    )}>
      <CardHeader className="pb-3">
        <div className="flex items-center justify-between">
          <CardTitle className="flex items-center space-x-2">
            <User className="w-5 h-5 text-pink-600" />
            <span>Compl√©tude du Profil</span>
          </CardTitle>
          {isComplete && (
            <div className="flex items-center space-x-1 text-green-600">
              <Sparkles className="w-4 h-4" />
              <span className="text-sm font-medium">Parfait !</span>
            </div>
          )}
        </div>
      </CardHeader>

      <CardContent className="space-y-4">
        {/* Status */}
        <div className="flex items-center space-x-3">
          {getStatusIcon()}
          <div>
            <h3 className={cn("font-semibold", getStatusColor())}>
              {getStatusMessage()}
            </h3>
            <p className="text-sm text-gray-600">{getMotivationalMessage()}</p>
          </div>
        </div>

        {/* Progress Bar */}
        <div className="space-y-2">
          <div className="flex justify-between items-center">
            <span className="text-sm font-medium text-gray-700">
              Progr√®s: {score}/{maxScore} points
            </span>
            <div className="flex items-center space-x-2">
              <TrendingUp className="w-4 h-4 text-green-500" />
              <span className="text-2xl font-bold text-pink-600">
                {percentage}%
              </span>
            </div>
          </div>

          <div className="relative">
            <Progress 
              value={percentage} 
              className="h-3"
            />
            <div 
              className={cn(
                "absolute top-0 left-0 h-3 rounded-full bg-gradient-to-r transition-all duration-500",
                getProgressColor()
              )}
              style={{ width: `${percentage}%` }}
            />
