            <div className="flex items-center justify-between mb-4">
              <div>
                <h2 className="text-2xl font-bold text-gray-800">
                  Bonjour, {user?.user_metadata?.first_name || user?.email?.split('@')[0] || 'User'}!
                </h2>
                <p className="text-gray-600 text-sm">
                  {user?.profile?.gender === 'female' ? 'D√©couvrez des profils honn√™tes' : 'Votre profil est visible'}
                </p>
              </div>
              <div className="flex items-center space-x-2">
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => setShowChallengeModal(true)}
                  className="flex items-center space-x-2"
                >
                  <Icons.Sparkles className="w-4 h-4" />
                  <span>Mini-D√©fi</span>
                </Button>
                <div className="text-right">
                  <p className="text-sm text-gray-500">
                    {user?.profile?.gender === 'female' ? '√âvaluations donn√©es' : 'Score moyen'}
                  </p>
                  <p className="text-2xl font-bold text-pink-500">
                    {user?.profile?.gender === 'female' ? myRatings.length : '4.2'}
                  </p>
                </div>
              </div>
            </div>

            {/* Trust Score Info */}
            <div className="gradient-trust rounded-xl p-4 text-white mb-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm opacity-90">
                    {user?.profile?.gender === 'female' ? 'Votre contribution' : 'Votre statut'}
                  </p>
                  <p className="text-xl font-bold">
                    {user?.profile?.gender === 'female' ? 'Experte en √âvaluation' : 'Profil V√©rifi√©'}
                  </p>
                </div>
                <div className="text-right">
                  <div className="flex items-center space-x-1">
                    <Icons.Star className="w-4 h-4 text-yellow-400" />
                    <span className="text-lg font-bold">4.8</span>
                  </div>
                  <p className="text-xs opacity-80">
                    {user?.profile?.gender === 'female' ? 'Fiabilit√©' : '√âvaluations'}
                  </p>
                </div>
              </div>
            </div>
          </div>

          {/* Profile Completion Card */}
          <div className="mb-6">
            <ProfileCompletionCard 
              compact={true}
              onImprove={() => window.location.href = "/profile"}
            />
          </div>

          {/* Profile Cards */}
          {profiles.length > 0 ? (
            <div className="space-y-4">
              {profiles.map((profile) => (
                <ProfileCard
                  key={profile.id}
                  profile={profile}
                  onRate={() => handleRate(profile)}
                  canRate={user?.profile?.gender === 'female'}
                />
              ))}
            </div>
          ) : (
            <Card>
              <CardContent className="p-8 text-center">
                <p className="text-gray-600">No more profiles to show</p>
                <Button 
                  onClick={() => refetch()}
                  className="mt-4 gradient-primary text-white"
                >
                  Refresh
                </Button>
              </CardContent>
            </Card>
          )}
        </div>
      </main>

      {/* Rating Modal */}
      <RatingModal
        isOpen={showRatingModal}
        onClose={() => setShowRatingModal(false)}
        profile={selectedProfile}
        onRatingSubmitted={handleRatingSubmitted}
      />

      {/* Mini Challenge Modal */}
      <MiniChallengeModal
        isOpen={showChallengeModal}
        onClose={() => setShowChallengeModal(false)}
        onChallengeComplete={() => {
          toast({
            title: "D√©fi termin√© !",
            description: "Votre r√©ponse aidera √† d√©marrer des conversations",
          });
        }}
      />

      {/* Bottom Navigation */}
      <Navigation />
    </div>
  );
}

