import { useState, useEffect } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { isUnauthorizedError } from "@/lib/authUtils";
import { Navigation } from "@/components/Navigation";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Icons } from "@/lib/icons";

export default function Premium() {
  const { user, isAuthenticated, isLoading } = useAuth();
  const { toast } = useToast();


  const { data: subscriptionStatus, refetch: refetchSubscription } = useQuery({
    queryKey: ["/api/subscription-status"],
    enabled: isAuthenticated && !isLoading,
  });

  const createCheckoutSession = useMutation({
    mutationFn: async () => {
      const res = await apiRequest("POST", "/api/subscribe");
      return res.json();
    },
    onSuccess: (data) => {
      if (data.url) {
        window.location.href = data.url;
      } else if (data.message === "Already subscribed") {
        toast({
          title: "D√©j√† abonn√©",
          description: "Vous avez d√©j√† un abonnement Premium actif",
        });
        refetchSubscription();
      } else {
        toast({
          title: "Erreur",
          description: "Impossible de rediriger vers Stripe",
          variant: "destructive"
        });
      }
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Non autoris√©",
